(function(){$(document).ready(function(){var b=document.querySelectorAll(".youtube");for(var a=0;a<b.length;a++){var c="https://img.youtube.com/vi/"+b[a].dataset.embed+"/hqdefault.jpg";var d=new Image();d.src=c;d.addEventListener("load",function(){b[a].appendChild(d)}(a));b[a].addEventListener("click",function(){var e=document.createElement("iframe");e.setAttribute("frameborder","0");e.setAttribute("allowfullscreen","");e.setAttribute("src","https://www.youtube.com/embed/"+this.dataset.embed+"?rel=0&showinfo=0&autoplay=1");this.innerHTML="";this.appendChild(e)})}})})();